package com.sample;

import static org.junit.jupiter.api.Assertions.assertEquals;

import com.sample.hackerrank.CountBinarySubstrings;
import org.junit.jupiter.api.Test;

class CountBinarySubstringsTest {

	@Test
	void testGetSubstringCount() {
		assertEquals(0, CountBinarySubstrings.getSubstringCount("000000"));
		assertEquals(6, CountBinarySubstrings.getSubstringCount("001100011"));
		assertEquals(3, CountBinarySubstrings.getSubstringCount("000110"));
		assertEquals(8, CountBinarySubstrings.getSubstringCount("010101010"));
		assertEquals(
			2658,
			CountBinarySubstrings.getSubstringCount(
				""
			)
		);
		assertEquals(
			798,
			CountBinarySubstrings.getSubstringCount(
				""
			)
		);
		assertEquals(
			6862,
			CountBinarySubstrings.getSubstringCount(
				""
			)
		);
	}
}
